INSERT INTO `games` VALUES ('2095', 'The Simpsons - Hit & Run', null, '0', '11/11/2003', 'Radical Entertainment', 'Vivendi Games', null, '|Action|', null, 'There are strange happenings in Springfield. People are missing, very mysterious crop circles are appearing, and hidden cameras are being exposed. When Homer notices a black van with a satellite dish parked outside the Simpsons house, he decides to investigate further into these odd events. What he reveals could be the biggest mystery to rock Springfield since Who Shot Mr. Burns?\r\n\r\nThe Simpsons Hit & Run promises a hilarious mission based driving game with exciting out-of-the-car platform gameplay, the signature satire of The Simpsons, and the interactive world of Springfield.', '0', null, '1291385400', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1793', 'D-Force', null, '1', '12/20/1991', 'Asmik', 'Asmik', null, '|Shooter|', null, 'Gameplay involves being the pilot of an Apache Helicopter, and shooting enemies down in the style of a vertical scrolling shooter. Large red gunships can be shot down to gain power-ups for the helicopter in order to upgrade the guns and have it fire homing missiles. Each level features a midboss and a boss, and both must be destroyed in order to advance to the next level. The style of the levels as the player advances alternates between \"Shooting Mode\" and \"Exploration Mode,\" where the latter involves levels set in a fantasy-like setting and are the only level types that provide no power-ups to the player and give the player the ability to switch their altitude.', '0', null, '1306266289', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
