INSERT INTO `games` VALUES ('1812', 'Vancouver 2010', null, '0', '01/15/2010', 'Eurocom Entertainment Software', 'Sega', null, null, null, 'Vancouver 2010 features realistic next-gen visuals and will provide players with an authentic simulation of 14 events from the upcoming Vancouver 2010 Olympic Winter Games giving gamers the opportunity to compete in, and get an early look at, many of the official venues. The online functionality of all three platforms will ensure that fans of the Olympic Winter Games will be able to experience the thrill of competing against other gamers across the globe for national pride. The game will also incorporate innovative control schemes that allow novice gamers to learn how to play the game, while providing experienced gamers the chance to set new records.', '0', null, '1291386221', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
