INSERT INTO `games` VALUES ('1904', 'Town & Country Surf Designs 2: Thrilla\'s Surfari', null, '0', '03/01/1992', 'Sculptured', 'LJN Ltd.', null, '|Sports|', null, 'Whoa, Thrilla Gorilla! Bag your boards and lay back tall for the raddest, weirdest surf and skate action ever! Wazula, an evil Kahuna, has stolen your babe and taken her to Africa. No curbs and quarter-pipes here, just the ultimate skate challenge of gnarly jungles and bakin\' deserts! It\'s hardcore surfin\' down killer volcanoes and mammoth waterfalls... ever catch a wave riding a shark? Wazula\'s got some smooth moves of his own, like a two-headed flying Rhino, a saw-toothed Great White, and a way-tough Lava Monster big as a volcano. So get rippin\' through the scorpions, slashin\' through the rapids, and trash that Wazula doofus! But don\'t choke, Thrilla... or your babe\'s toast.', '0', null, '1291068328', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('1905', 'Tecmo Super Bowl', null, '0', '12/01/1991', 'Tecmo', 'Tecmo', null, '|Sports|', null, 'The teams, the players, and the halftime shows are ready for the ultimate football season, and you\'re in control. From the first pre-season game to the playoffs, it\'s your call, your skill, and your team. Can you get to and win the Super Bowl? Tecmo Super Bowl is the only football game to feature onside kicks, blocked passes and kicks, fumbles, pitchout to the running back, player injuries, time-outs, and much, much more! For 1 or 2 players. All 28 NFL teams! Four running plays, four passing plays. Team stats and data. Create your own Pro Bowl. Coach Mode, where you call the plays!', '0', null, '1291061147', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
