INSERT INTO `games` VALUES ('1914', 'Final Fight 3', null, '2', '12/22/1995', 'Capcom', 'Capcom', null, '|Fighting|', null, 'Following the annihilation of the Mad Gear Gang, a new criminal element named the Skull Cross Gang emerges as the new dominant street gang in Metro City. When Guy returns to Metro City from his martial art training to become reacquainted with his friend Mike Haggar, the Mayor of Metro City, the two are suddenly informed that the Skull Cross Gang has started a riot in the downtown area of the city. Joined by Lucia, a detective in the Metro City Police\'s Special Crimes Unit, and Dean, a street fighter whose family was murdered by the Skull Cross Gang, Guy and Haggar must once again save Metro City from its newest menace.', '0', null, '1305452341', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
