INSERT INTO `games` VALUES ('2094', 'Ninja Gaiden Trilogy', null, '0', '08/10/1995', 'Team Ninja', 'Tecmo', null, '|Action|', null, 'Here it is! The complete NINJA GAIDEN TRILOGY you asked for. Enjoy the 3 classic Nintendo Ninja Gaiden games on one Super NES Game Pak.\r\nFollow Ryu\'s adventures as he fights the ancient forces of darkness with his Ninjutsu powers. Use strategy, cunning and deception to acquire the special ninja weapons needed to be successful in this epic quest.\r\nThe Ninja Gaiden series tells it\'s story through Tecmo\'s unique cinema screen animation. This one is not to be missed!', '0', null, '1291174656', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
