INSERT INTO `games` VALUES ('2259', 'Torchlight', null, '1', '01/01/2010', 'Runic Games', 'Perfect World', null, '|Construction and Management Simulation|', null, 'In the fantasy world that serves as the setting of Torchlight, Ember is a mysterious ore which has the power to imbue people and items with magical power. The mining boomtown called Torchlight is built above a rich vein of Ember, and adventurers are drawn there seeking the magical substance and the enchanted items it creates. However, as the player character explores the dungeons below Torchlight, they discover that Ember has a corrupting influence which led to the fall of past civilizations and endangers those who use it in the present.', null, null, '1306546387', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
