INSERT INTO `games` VALUES ('2301', '1080° Avalanche', null, '0', '11/28/2003', 'NST', 'Nintendo', null, '|Racing|Sports|', null, '1080° Avalanche is a snowboarding game for the Nintendo GameCube, developed by Nintendo\'s in-house development studio, NST, and published by Nintendo. Avalanche is a sequel to 1080° Snowboarding for the Nintendo 64. The game has an emphasis on racing, rather than doing tricks, in contrast to other popular snowboarding games, such as the SSX series. It can output in 480p and Dolby Pro Logic II and supports four players on one GameCube as well as LAN play with up to four GameCubes.', '0', null, '1293948652', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '2', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('2302', 'Donkey Kong Jungle Beat', null, '1', '12/16/2006', 'Nintendo EAD Tokyo', 'Nintendo', null, '|Adventure|', null, 'Donkey Kong Jungle Beat is a Nintendo GameCube video game featuring the ape Donkey Kong and played with the DK Bongos. It was released in Japan on December 16, 2004, in Europe on February 4, 2005, in North America on March 14, 2005, and in Australia on March 17, 2005. In 2008 and 2009, the game was re-released in the New Play Control! series of revamped Nintendo GameCube titles. The Wii version of Donkey Kong Jungle Beat is somewhat changed from the original GameCube version, including new levels, modifications of old levels, and traditional controls that have the player use the analog stick to move and the A button to jump, unlike the GameCube version which required the player to beat the bongos to do both. This is the first game to be rated E10 by the ESRB.', null, null, '1306636116', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '2', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('2303', 'Sonic Colors', null, '0', '11/11/2010', 'Sonic Team', 'Sega', null, '|Adventure|', null, 'Sonic Colors, titled Sonic Colours in some markets is a platform game in the Sonic the Hedgehog series. It was first announced on May 26, 2010, in a press release by Sega for Italy, and it included a teaser trailer. The game was released for the Wii and Nintendo DS on November 11, 2010, in Australia, November 12, 2010, in Europe, November 16, 2010, in North America[4] and November 18, 2010, in Japan.', '0', null, '1293950228', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '9', null, '', '', '', '', '', '', null, null, null, null);
