INSERT INTO `games` VALUES ('2305', 'Kirby\'s Block Ball', null, '0', '12/14/1995', 'Nintendo', 'Nintendo', null, '|Puzzle|', null, 'Kirby bounces around a field littered with blocks and enemies. He can destroy the blocks and enemies by touching them. The object of the game is to keep Kirby in play by bouncing him against four paddles on the top and bottom, and left and right sides of the screen. When all blocks are eliminated, Kirby moves onto the next level. Enemies destroyed by Kirby are turned into tasty treats, which give the player bonus points if obtained. There are also several copy abilities to be found if Kirby hits certain enemies, which aid him in destroying blocks.', '0', null, '1294021143', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '4', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('2306', 'Pokémon FireRed Version', null, '0', '01/29/2004', 'Gamefreak', 'Nintendo', null, '|Role-Playing|', null, 'Pokémon FireRed and LeafGreen take place mostly in the fictional region of Kanto. This is one distinct region of many in the Pokémon world, which includes varied geographical habitats for the Pokémon species, human-populated towns and cities, and routes between locations. Some areas are only accessible once the player acquires a special item or one of the player\'s Pokémon learns a special ability. Near the end of the plot, the protagonist is able to venture to the Sevii Islands, a new area not available in the original Red or Blue games. It consists of an archipelago of seven islands that contain Pokémon normally exclusive to the Johto region.', null, null, '1296446855', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '5', null, '', '', '', '', '', '', null, null, null, null);
