INSERT INTO `games` VALUES ('2308', 'Evil Genius', null, '1', '28/09/2004', 'Elixir Studios', 'Sierra Entertainment', null, '|Strategy|', null, 'Evil Genius is a single player real-time strategy and simulation video game developed by Elixir Studios and published by Sierra Entertainment, released on 28 September 2004. The game is inspired by the spy thriller genre (notably the James Bond film series). Similarly to Dungeon Keeper, the game turns the traditional plotline on its head, with the player acting as the villain, evading the comically stereotyped forces of justice. Gameplay revolves around the player building an island fortress and achieving clichéd world domination.', '0', null, '1299320209', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
