INSERT INTO `games` VALUES ('2312', 'Rise of Nations', null, '0', '05/20/2003', 'Big Huge Games', 'Microsoft Game Studios', null, '|Strategy|', null, 'Rise of Nations is a complex real-time strategy game developed by Big Huge Games.  Based in a historical context, it features gameplay from well before recorded history to the present spread across eight ages.  The game is notable for its efforts at combining elements of traditional real-time and turn-based strategy games -- examples include the Conquer the World single player campaign, which plays like a game of Risk with a short game taking the place of rolling dice, and the concept of territories.', '0', null, '1294037201', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('2313', 'Dead To Rights', null, '1', '11/18/2002', 'Namco', 'Namco', null, '|Action|Shooter|', null, 'Meet Jack Slate, a cop framed for murder and facing execution. Slate escapes from Iron Point Maximum-Security Penitentiary to bring down the criminals that framed. In Dead to Rights, you play the role of Slate, as he fights through the hard streets and corrupt enemies of Grant City. There are plenty of ways go about bringing the conspirators to justice--Jack can perform disarm moves, shoot different weapons, pick locks, fight hand-to-hand, and play minigames such as arm wrestling, bench press, and dancing. In this gritty, crime-noir inspired epic, you\'re always on the run.', null, null, '1306620874', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '11', null, '', '', '', '', '', '', null, null, null, null);
