INSERT INTO `games` VALUES ('2384', 'Braid', null, '1', '08/06/2008', 'Number None Inc.', 'Number None Inc.', null, '|Action|Adventure|Puzzle|', null, 'Braid is a puzzle-platformer, drawn in a painterly style, where the player manipulates the flow of time in strange and unusual ways. From a house in the city, journey to a series of worlds and solve puzzles to rescue an abducted princess. In each world, you have a different power to affect the way time behaves, and it is time\'s strangeness that creates the puzzles. The time behaviors include: the ability to rewind, objects that are immune to being rewound, time that is tied to space, parallel realities, time dilation, and perhaps more. Braid treats your time and attention as precious; there is no filler in this game. Every puzzle shows you something new and interesting about the game world.', '0', null, '1301100648', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
