INSERT INTO `games` VALUES ('2430', 'Serious Sam - The First Encounter', null, '0', '12/01/2001', 'Croteam', 'Gathering of Developers', null, '|Shooter|', null, 'In ancient times, Earth was involved in a conflict between Mental and the Sirians, an alien race that left many of its artifacts to be found by humanity. In the 22nd century Mental\'s forces return to Earth hell-bent on eradicating humankind: as a last resort the usage of the \"Time Lock\" is decided: this Sirian device can send back through time a single individual who can, hopefully, defeat Mental and alter the course of history. Because of his bravery in fighting the monsters, Sam \"Serious\" Stone is chosen to use the \"Time Lock\".', null, null, '1306622520', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('2431', 'Serious Sam - The Second Encounter', null, '0', '02/01/2002', 'Croteam', 'Gathering of Developers', null, '|Shooter|', null, 'The Second Encounter starts where the previous chapter left off, with Sam travelling to Sirius on the starship. Unfortunately Sam\'s vehicle is accidentally hit by the \"Croteam crate-bus\" and plummets down to Earth\'s surface. Serious Sam crash lands in Central America in the Mayan age. However not all is lost, since the Sirians left a back-up ship on Earth. Since this vehicle is located in a different age from the one Sam finds himself, the hero has to uncover the locations of a number of Sirian time-gates that will help him reach his destination.', null, null, '1306622493', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
