INSERT INTO `games` VALUES ('2543', 'Aguri Suzuki - F-1 Super Driving', null, '2', '04/01/1993', 'Genki', 'Altron', null, '|Racing|', null, 'Released as Redline F-1 Racer in North America and in Europe and Japan under its original title, is a Formula One simulator for the Super Nintendo Entertainment System. Everything from treacherous right turns to gasoline is simulated as the player tries to win the FIA World Drivers\' and Constructors\' Championships. The player can play a single race (non-championship Formula One race) or a season mode where the winner takes all, and the losers can wait until next year.\r\n \r\nThe game allows players to customize their racing vehicle. Suspension, wings, and brakes among other things can be altered to gain lap times in addition to positions on the track. Winning is near impossible unless the player can successfully tinker with his vehicle from the beginning of the race week. Even the weather can betray the player; having the wrong tires will make the car skid out. The controls and overall gameplay are relatively simplistic for a driving simulator game. Aguri Suzuki even appears in the game as the most expert driver. The graphics in the game look like they come out of an 8-bit console system.', null, null, '1306545365', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('2544', 'Redline F-1 Racer', null, '2', '08/01/1993', 'Genki', 'Absolute Entertainment', null, '|Racing|', null, 'Released in Europe and Japan under its original title, \"Aguri Suzuki F-1 Super Driving\" is a Formula One simulator for the Super Nintendo Entertainment System. Everything from treacherous right turns to gasoline is simulated as the player tries to win the FIA World Drivers\' and Constructors\' Championships. The player can play a single race (non-championship Formula One race) or a season mode where the winner takes all, and the losers can wait until next year.\r\n \r\nThe game allows players to customize their racing vehicle. Suspension, wings, and brakes among other things can be altered to gain lap times in addition to positions on the track. Winning is near impossible unless the player can successfully tinker with his vehicle from the beginning of the race week. Even the weather can betray the player; having the wrong tires will make the car skid out. The controls and overall gameplay are relatively simplistic for a driving simulator game. Aguri Suzuki even appears in the game as the most expert driver. The graphics in the game look like they come out of an 8-bit console system.\r\n \r\nIn 1993, Aguri Suzuki F-1 Super Driving was released for the Game Boy handheld, exclusively in Japan. In this game, the player can control a kart and a Formula One car.', null, null, '1306623141', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
INSERT INTO `games` VALUES ('2545', 'Broken Sword II', null, '1', '10/31/1997', 'Revolution Software', 'Sony Computer Entertainment', null, '|Adventure|', null, 'The second game in the series, released in 1997, opens with George Stobbart witnessing the kidnapping of his now girlfriend Nico Collard. It is up to George to break Nico out, and uncover a secret drug ring that relates to the redemption of a sinister Mayan god. It is also the only game in the series that does not follow the Knights Templar story line.\r\n\r\nThe game was released as Broken Sword: The Smoking Mirror in North America.', null, null, '1306564920', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
