INSERT INTO `games` VALUES ('2585', 'Super Street Fighter II', null, '2', null, null, null, null, '|Fighting|', null, 'is a head-to-head fighting game produced by Capcom originally released as a coin-operated arcade game in 1993. It is the fourth game in the Street Fighter II sub-series of Street Fighter games, following Street Fighter II: Hyper Fighting. In addition to refining and balancing the existing character roster from the previous installments, Super Street Fighter II also introduced four new characters. It was also the first game to be developed on Capcom\'s CP System II hardware, which permitted more sophisticated graphics and audio over the original CP System hardware, used by the previous versions of Street Fighter II.', null, null, '1306661635', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '18', null, '', '', '', '', '', '', null, null, null, null);
