INSERT INTO `games` VALUES ('2616', 'Darkest of Days', null, '1', '09/08/2009', '8monkey Labs', 'Phantom EFX | Valcon Games', null, null, null, 'Have you ever wondered what could happen if time travel was a reality? Have you ever thought about the possibility of going back in time to rewrite history for the better? That possibility is a reality in Darkest of Days, where players will travel back and forth through the annals of time to relive some of mankind\'s most dire hours.\r\n\r\nBy fighting through terrible events such as The Battle of Antietam, the bloodiest conflict of the Civil War, and both World War I and II, the player will attempt to save certain key individuals who were never meant to become involved in these truly monstrous events and thereby change human history for the better.', null, null, '1306620770', null, null, 'M - Mature', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
