INSERT INTO `games` VALUES ('2630', 'Hoyle Puzzle and Board Games 2010', null, '4', '12/15/2009', 'Encore Software, Inc.', 'Casino', null, '|Puzzle|Strategy|', null, 'Throw the dice and shuffle those cards, it\'s Game Night! HOYLE Puzzle & Board Games delivers hours of fun with the biggest assortment of games yet!\r\n\r\nThis great collection features traditional game standards like Hangman, Mahjongg and Anagrams plus popular hits like Chess, Battling Ships and Sudoku. Challenge your friends and family on one computer or play against the witty and personable HOYLE characters! All games are designed with rules according to HOYLE, the Official Name in Gaming.\r\n\r\nHOYLE Highlights:\r\n\r\n    Use HOYLE Face Creator to design your own player\r\n    Customize card backs, characters and more\r\n    Earn HOYLE rewards to unlock fun game bonuses\r\n\r\nNEW Big Bang Brain Games:\r\n\r\n    Echo\r\n    Fallacy\r\n    Nova Sweeper\r\n    Remembrance and More!', '0', null, '1305818596', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '1', null, '', '', '', '', '', '', null, null, null, null);
