INSERT INTO `games` VALUES ('2775', 'Viewtiful Joe', null, '0', null, 'Clover Studio', 'Capcom', null, '|Action|Adventure|', null, 'Joe is no ordinary man and Viewtiful Joe is no ordinary game. Capcom\'s new superhero action game mixes funky cartoon-style visuals with classic side-scrolling gameplay and introduces the world\'s quirkiest million dollar action hero. More than just any ordinary dude, Joe must transform into the ultimate superhero. It\'s up to you to activate the correct view mode like \"slow\" or \"zoom in\" in order to clobber your enemies with beautiful style. You can also speed up or slow down your visual effects for even more \"viewtiful\" moves. Viewtiful Joe mixes an innovative viewpoint with an amazing stunt-filled action movie universe.', null, null, '1306624889', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '11', null, '', '', '', '', '', '', null, null, null, null);
