INSERT INTO `games` VALUES ('2782', 'The Itchy & Scratchy Game', null, '1', null, 'Bits Studios', 'Acclaim Entertainment', null, '|Action|', null, 'Itchy & Scratchy is a single player platform side-scroller. The player controls Itchy, the mouse, and must defeat Scratchy, the cat, with a variety of weapons. Itchy caries a default mallet, but can pick up additional weapons on particular levels. Each of the seven levels takes place in a different fictional location and is designed as a maze of doors and platforms. After defeating Scratchy, he usually will return with a special contraption to attack Itchy, and must therefore be defeated again.', null, null, '1306626693', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
