INSERT INTO `games` VALUES ('2785', 'The Hunt For Red October', null, '0', null, 'Beam Sofrware', 'Hi-Tech Expressions', null, '|Action|Shooter|', null, 'Based on the movie of the same name, the Soviet Union have been building in secret a Typhoon-class submarine with nuclear strike capabilities. This war machine has been named the Red October. While on its trial run, the captain, Marko Ramius has hijacked the submarine along with it\'s crew. They plan to defect to the United States. Naturally, such a weapon must not be allowed to fall into American plans, and so orders are issued among the Soviet forces to destroy the Red October.', '0', null, '1306627275', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
