INSERT INTO `games` VALUES ('2792', 'Super Street Fighter II: The New Challengers', null, '2', null, 'CAPCOM', 'CAPCOM', null, '|Fighting|', null, 'Super Street Fighter II: The New Challengers is a head-to-head fighting game produced by Capcom originally released as a coin-operated arcade game in 1993. It is the fourth arcade version of Street Fighter II produced, following the original Street Fighter II, Street Fighter II: Champion Edition and Street Fighter II: Hyper Fighting. In addition to refining and balancing the existing character roster from the previous installments, Super Street Fighter II also introduced four new characters. It was also the first game to be developed on Capcom\'s CP System II hardware, which permitted more sophisticated graphics and audio over the original CP System hardware, used by the previous versions of Street Fighter II.', null, null, '1306628960', null, null, 'T - Teen', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
