INSERT INTO `games` VALUES ('2811', 'NBA Live 2000', null, '0', null, 'EA Sports', 'EA Sports', null, null, null, 'The NBA Live series of basketball video games, published by EA Sports, is currently one of the leading National Basketball Association simulations on the market.\r\n\r\nStarting from NBA Live 2000, the series featured NBA Live Legend All-Stars Teams, that included some biggest names from five decades (50s to 90s). These teams could be used instantly, but to use the players as regular players (e.g. traded, played on regular NBA Teams) they needed to be unlocked. Along the series, some of the rosters were changed due to many reasons as Michael Jordan was on the 90\'s team through 2004.', null, null, '1306630407', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '3', null, '', '', '', '', '', '', null, null, null, null);
