INSERT INTO `games` VALUES ('2813', 'NBA Jam Tournament Edition', null, '0', null, 'Iguana Entertainment', 'Acclaim', null, '|Sports|', null, 'NBA JAM Tournament Edition brings you more senses-shattering slam dunking excitement than ever! More than twice as many NBA superstars, more than double the secret characters, Hot Spots, Super Jam Power-Ups, battery back-up and - OH MY - nine all-new rim-rattling slam dunks plus all the original jams! NBA JAM Tournament Edition... it\'s on FIRE!', null, null, '1306630497', null, null, 'E - Everyone', '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '6', null, '', '', '', '', '', '', null, null, null, null);
