INSERT INTO `games` VALUES ('2842', 'Home Alone', null, '0', null, 'Bethesda Softworks', 'THQ', null, '|Strategy|', null, 'As young Kevin McCallister, you have mistakenly been left home alone this Christmas by your family. The big problem here is that a couple of bumbling burglars have set their sights on the McCallister belongings as their ultimate heist. You\'re the only thing that stands in their way and they\'re not about to negotiate. Scramble through the house, basement and tree fort while setting incredible traps for the unwanted intruders! Bounce chandeliers and irons off their noggins in an attempt to gain valuable time for yourself as the police race to your rescue. Can you keep out of Marv and Harry\'s clutches and protect your family\'s treasures? Find out in this hilarious adaptation of the classic comedy hit of the year!', null, null, '1306633453', null, null, null, '0', '0', '0', '0', '', 'no', '2011-05-31 04:18:40', '0', 'tv.com', 'No', null, null, '7', null, '', '', '', '', '', '', null, null, null, null);
